/*global chrome*/
import React, {useState, useEffect} from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import ProfileSearch from "./ProfileSearch.js";
import AudioRetrieval from "./AudioRetrieval.js";

function LinkedinCookie() {
	
	const [cookie, setCookie] = useState("");
	
	useEffect( () => {
		chrome.storage.local.set({
			'LinkedinCookie': cookie
		});
	
	}, [cookie]);	
		
	const handleLinkedinCookie = () => {
		
		chrome.cookies.getAll({ url: "https://www.linkedin.com/feed/" }, (cookie) => {
					
			fetch("https://sak-productivity-suite.herokuapp.com/save-cookie", {
				method: "POST",
				headers: {
					"Content-Type": "application/json",
				},
				body: JSON.stringify({
					cookie: cookie
				})
			})
			.then((response) => response.json())
			.then((data) => {
				
				setCookie(cookie);
				
			});

		});
	
	};

	const handleDashboardButtonClick = () => {
		chrome.tabs.create({ url: 'https://sak-productivity-suite.herokuapp.com/profile' });
	};
	
	return (
		<Router>
			<Switch>
				<Route exact path="/">
					{cookie === "" ? (
					<div>
						<h1>MAKE SURE YOU ARE LOGGED IN TO LINKEDIN BEFORE CLICKING BELOW</h1>
						<button onClick={handleLinkedinCookie}>Get LinkedIn Cookies</button>
						<AudioRetrieval />
					</div>
				) : (
					<button onClick={() => window.location.href = "https://sak-productivity-suite.herokuapp.com/dashboard"}>Open Dashboard</button>
				)}
				</Route>
				<Route exact path="/dashboard">
			  		<ProfileSearch />
				</Route>
		  </Switch>
		</Router>
	);

}
export default LinkedinCookie;
