/*global chrome*/
import React, {useState, useEffect} from "react";


function AudioRetrieval() {
	
	const [transcriptTextArea, setTranscriptTextArea] = useState([]);
				
	const handleStartCapture = () => {
		// Get the current tab's audio stream
		chrome.tabCapture.capture({audio: true}, function(stream) {

			const audioContext = new AudioContext();

			// Create a MediaElementAudioSourceNode from the stream
			const sourceNode = audioContext.createMediaStreamSource(stream);

			// Create a ScriptProcessorNode for processing the audio data
			const scriptNode = audioContext.createScriptProcessor(2048, 1, 1);

			// Connect the nodes
			sourceNode.connect(scriptNode);
			scriptNode.connect(audioContext.destination);

			// Set up the speech recognition object
			const SpeechRecognition = window.speechRecognition || window.webkitSpeechRecognition;
			const recognition = new SpeechRecognition();

			recognition.continuous = true;
			recognition.interimResults = true;
			recognition.lang = 'en-US';

			// Process the audio data in the scriptNode's onaudioprocess callback
			scriptNode.onaudioprocess = function(audioProcessingEvent) {
				const inputBuffer = audioProcessingEvent.inputBuffer;

				// Process the audio data in the inputBuffer
				// ...
				try {
					// Start the speech recognition
					recognition.start();

					// Process the speech recognition results
					recognition.onresult = function(event) {
						console.log(event);
						const transcript = event.results[event.results.length - 1][0].transcript;
						console.log(transcript);
						setTranscriptTextArea(transcript);
					}
				}catch(error){
					console.log(error);
				}
			};

			
		});
		
	};
	
	const handleTranscriptTextAreaChange = (event) => {
		setTranscriptTextArea(event.target.value);
	};
	
	return (
		<>
		<button onClick={handleStartCapture}>
			Start audio capture
		</button>
		<textarea value={transcriptTextArea} onChange={handleTranscriptTextAreaChange} placeholder="The transcript will appear here"></textarea>
		</>
		
	);

}
export default AudioRetrieval;
