import { Routes, Route, useLocation, useState } from 'react-router-dom';
import DisplayConvos from './DisplayConvos';
import ProfileSearch from './ProfileSearch';
import NavbarComponent from './NavbarComponent';

function App() {

    const { search } = useLocation();
    const [sessionId, setSessionId] = useState("");

    // const cookie = {};
    // for (const [key, value] of searchParams.entries()) {
    //     cookie[key] = value;
    // }
    // console.log(cookie);

    const val = search.split("=")[1];
    console.log(val);

    useEffect(() => {
		
		if (pageArray[pageIndex]) {
		
			const userProfile = pageArray[pageIndex][0];
			setUserProfile(userProfile);
			
			setFullName(userProfile["full_name"]);
			setLatestTitle(userProfile["latest_title"]);
			setProfileId(userProfile["profile_id"]);
			setPublicId(userProfile["public_id"]);
			setProfileUrn(userProfile["profile_urn"]);
			
			setNoteTextArea("");
			
			setPeopleInterestsArray([]);
			setCompanyInterestsArray([]);
			setActivityInterestsArray([]);
			
			setSelectedInterests("");
		}
		
	}, [val]);

    return (
        <>
        <NavbarComponent />
        <Routes>
            <Route path="/linkedin-search" element={<ProfileSearch sessionId={sessionId} />} />
            <Route path="/linkedin-messages" element={<DisplayConvos />} />
        </Routes>
        </>
    );
}
export default App;
